$(function() {
  
  // inizialize bookmark
  var current = window.location.hash ? window.location.hash : 'BODY';
  $('nav .nav a[href=' + current + ']').parent().addClass('active');
  
  // click and go
  $('nav .nav a').click(function(e){
    e.preventDefault();
    var current = $(this).attr('href');
    $.scrollTo(current, 1000, {
      easing: 'easeInOutCubic',
      onAfter: function(){
        location.hash = current;
      }
    })
  })
  
  // resize stuff
  $(window).resize(function(){
    $('[data-spy="scroll"]').each(function () {
      var $spy = $(this).scrollspy('refresh');
    });
    var current = $(' nav .nav li.active a').attr('href');
    $.scrollTo(current);
    location.hash = current;
  })
    
});


